import { ThemeConsumer } from 'styled-components';

const size = {
  mobile: '770px',
  desktop: '1700px',
};

const theme = {
  mobile: `(max-width: ${size.mobile}`,
  desktop: `(min:${size.desktop})`,
  background: 'red',
};

export default theme;
