import { delay } from 'redux-saga';
import {all,takeLatest,fork, put} from 'redux-saga/effects';

import {TRANSLATE_SIMPLE_REQUEST,TRANSLATE_SIMPLE_SUCCESS,TRANSLATE_SIMPLE_FAILURE} from '../reducers/translate';

function translateSimpleAPI(data){
    return axios.post('/simple',data);
}

function* translateSimple(action){

    const result = yield call(translateSimpleAPI, action.data);
    yield delay(1000);
    try {
        console.log("done");
        yield put({
            type:TRANSLATE_SIMPLE_SUCCESS,
            data:"번역 성공!!!"

        });

    } catch (error) {
        console.log("fails");
        yield put({
            type:TRANSLATE_SIMPLE_FAILURE,
            error:error.response.data,

        });

    }

}



function* watchTranslateSimple(){
    yield takeLatest(TRANSLATE_SIMPLE_REQUEST,translateSimple);
}


export  default function* translateSaga(){

    yield all([fork(watchTranslateSimple)])

}
