import React from 'react';
import { Button } from 'semantic-ui-react';

import AppLayout from '../components/AppLayout';

const Template = () => {
  const [text, setText] = useState('');
  const [textOut, setTextOut] = useState('');
  const { simple, translateSimplesuccess } = useSelector(
    (state) => state.translate
  );
  const dispatch = useDispatch('');

  useEffect(() => {
    if (translateSimplesuccess) {
      setTextOut(simple[0].Output);
    }
  }, [translateSimplesuccess]);

  const onSubmit = useCallback(() => {
    console.log('submit');
    console.log(text);
    if (text.trim('') === '') {
      alert('내용을 입력하세요');
      return;
    }
    dispatch({ type: TRANSLATE_SIMPLE_REQUEST, data: text });
  }, [text]);
  const onChangeText = useCallback(
    (e) => {
      setText(e.target.value);
    },
    [text]
  );

  const onItemClick = useCallback(
    (Input, Output) => () => {
      setTextOut(Output);
      setText(Input);
    },
    []
  );

  const onClickCopy = useCallback(() => {}, []);

  const onClickRedo = useCallback(() => {
    setText('');
  }, []);
  return (
    <AppLayout>
      <div style={{ position: 'fixed', height: '100%', width: '100%' }}>
        <SimpleContainer>
          <SimpleCol>
            <Form style={{ height: '100%' }} onSubmit={onSubmit}>
              <TextArea
                style={{ height: '100%', resize: 'none' }}
                placeholder="텍스트를 입력하세요..."
                onChange={onChangeText}
                value={text}
                cols={10}
              />

              <Button
                style={{ bottom: '5px', right: '9px', position: 'absolute' }}
                position
                basic
                icon="exchange"
                type="submit"
                color="rgba(34,36,38,.15)"
              ></Button>
            </Form>
            <Button
              style={{ top: '35px', right: '39px', position: 'absolute' }}
              position
              basic
              icon="redo"
              onClick={onClickRedo}
              color="rgba(34,36,38,.15)"
            />
          </SimpleCol>

          <SimpleCol>
            <div
              style={{
                background: '#EBEBEB',
                height: '100%',
                padding: '.78571429em 1em',
                borderRadius: '.28571429rem',
                overflow: 'auto',
              }}
            >
              {textOut}
            </div>
            <Button
              icon="copy outline"
              style={{
                bottom: '35px',
                right: '36.5px',
                position: 'absolute',
                background: '#EBEBEB',
                Index: 1000,
              }}
              onClick={onClickCopy}
            />
          </SimpleCol>
        </SimpleContainer>
      </div>
    </AppLayout>
  );
};

export default Template;
