import React ,{useState,useCallback}from 'react';

import { useSelector,useDispatch } from 'react-redux';
import AppLayout from '../components/AppLayout';
import History from '../components/History';
import {TRANSLATE_SIMPLE_REQUEST} from '../reducers/translate';
import { Form, TextArea, Button, Icon, Divider, List, Segment } from 'semantic-ui-react'

const Main = () => {

    const [text, setText] = useState('');
    const dispatch = useDispatch('');
    const onSubmit=useCallback(
        () => {
           console.log('submit');
           console.log(text);
           if(text.trim('')===''){
               alert('내용을 입력하세요');
               return;
           }
           dispatch({type:TRANSLATE_SIMPLE_REQUEST,data:text});
           console.log(text);
        },
        [text],
    )
    const onChangeText=useCallback(

        (e) => {
           setText(e.target.value);
        },
        [text],
    )

    return (

        <AppLayout>

            <div class="ui grid" style={{ padding: "40px", height: "650px" }}>

                <div class="eight wide column">
                    <Form style={{ height: "100%" }} onSubmit={onSubmit}>
                        <TextArea style={{ height: "100%" }} placeholder='텍스트를 입력하세요...' onChange={onChangeText} value={text}/>

                        <Button icon labelPosition='left' type='submit'>
                            <Icon name='play' /> 번환
                        </Button>
                    </Form>



                </div>


                <div class="eight wide column">

                    <div style={{ background: "#EBEBEB", height: "100%", padding: '.78571429em 1em', borderRadius: '.28571429rem' }}>
                        <p>
                            Lorem ipsum dolor sit amet, consectetuer adipiscing elit. Aenean commodo
                            ligula eget dolor. Aenean massa strong. Cum sociis natoque penatibus et
                            magnis dis parturient montes, nascetur ridiculus mus. Donec quam felis,
                            ultricies nec, pellentesque eu, pretium quis, sem. Nulla consequat massa
                            quis enim. Donec pede justo, fringilla vel, aliquet nec, vulputate eget,
                            arcu. In enim justo, rhoncus ut, imperdiet a, venenatis vitae, justo.
                            Nullam dictum felis eu pede link mollis pretium. Integer tincidunt. Cras
                            dapibus. Vivamus elementum semper nisi. Aenean vulputate eleifend tellus.
                            Aenean leo ligula, porttitor eu, consequat vitae, eleifend ac, enim.
                            Aliquam lorem ante, dapibus in, viverra quis, feugiat a, tellus. Phasellus
                            viverra nulla ut metus varius laoreet. Quisque rutrum. Aenean imperdiet.
                            Etiam ultricies nisi vel augue. Curabitur ullamcorper ultricies nisi.
                     </p>


                    </div>



                </div>




            </div>


            <div> 검색내역</div>
            <Divider style={{ marginLeft: '40px', marginRight: '40px' }} />
            <div>
                <History />
            </div>

        </AppLayout >

    )
}

export default Main;
