import React, { useState, useCallback, useEffect } from 'react';

import { useSelector, useDispatch } from 'react-redux';
import AppLayout from '../components/AppLayout';
import { TRANSLATE_SIMPLE_REQUEST } from '../reducers/translate';
import {
  Form,
  TextArea,
  Button,
  Icon,
  Divider,
  Dropdown,
} from 'semantic-ui-react';
import styled from 'styled-components';
const SimpleCol = styled.div`
  width: 100%;
  padding: 10px 30px;
  height: 100%;
  position: relative;

  @media only screen and (max-width: 768px) {
    width: 100%;
    height: 50%;
    marin: 0 auto;
  }
`;

const SimpleContainer = styled.div`
  height: 80%;
  position: relative;
  display: flex;

  flex-direction: row;
  justify-content: space-between;
  @media only screen and (max-width: 768px) {
    height: 60%;
    display: block;
  }
`;

const Template = () => {
  const [textStart, settextStart] = useState('');
  const [textMiddle, setTextMiddle] = useState('');
  const [textEnd, setTextEnd] = useState('');
  const [text, setText] = useState('');
  const [textOut, setTextOut] = useState('');
  const { simple, translateSimplesuccess } = useSelector(
    (state) => state.translate
  );
  const dispatch = useDispatch('');

  useEffect(() => {
    if (translateSimplesuccess) {
      setTextOut(simple[0].Output);
    }
  }, [translateSimplesuccess]);

  const onSubmit = useCallback(() => {
    console.log('submit');
    // console.log(text);
    if (text.trim('') === '') {
      alert('내용을 입력하세요');
      return;
    }
    // dispatch({ type: TRANSLATE_SIMPLE_REQUEST, data: text });
  });
  const onChangeText = useCallback(
    (e) => {
      setText(e.target.value);
    },
    [text]
  );

  const onItemClick = useCallback(
    (Input, Output) => () => {
      setTextOut(Output);
      setText(Input);
    },
    []
  );

  const onClickCopy = useCallback(() => {}, []);

  const onClickRedo = useCallback(() => {
    setText('');
  }, []);

  const options = [
    { key: 1, text: 'Choice 1', value: 1 },
    { key: 2, text: 'Choice 2', value: 2 },
    { key: 3, text: 'Choice 3', value: 3 },
  ];
  return (
    <AppLayout>
      <Dropdown
        clearable
        options={options}
        selection
        style={{ marginTop: '10px', marginLeft: '30px' }}
      />
      <div style={{ position: 'fixed', height: '100%', width: '100%' }}>
        <SimpleContainer>
          <SimpleCol>
            <Form
              style={{ height: '100%', position: 'relative' }}
              onSubmit={onSubmit}
            >
              <div>
                <TextArea
                  style={{ height: '20%', resize: 'none', width: '100%' }}
                  placeholder="텍스트를 입력하세요..."
                  onChange={onChangeText}
                  value={textStart}
                />
                <TextArea
                  style={{
                    height: '70%',
                    resize: 'none',
                    marginTop: '15px',
                    width: '100%',
                  }}
                  placeholder="텍스트를 입력하세요..."
                  onChange={onChangeText}
                  value={textMiddle}
                />
                <TextArea
                  style={{
                    height: '20%',
                    resize: 'none',
                    marginTop: '15px',
                    width: '100%',
                  }}
                  placeholder="텍스트를 입력하세요..."
                  onChange={onChangeText}
                  value={textEnd}
                />
              </div>
            </Form>
            <Button
              style={{ top: '35px', right: '39px', position: 'absolute' }}
              position
              basic
              icon="redo"
              onClick={onClickRedo}
              color="rgba(34,36,38,.15)"
            />
            <Button
              style={{ top: '35px', right: '39px', position: 'absolute' }}
              position
              basic
              icon="redo"
              onClick={onClickRedo}
              color="rgba(34,36,38,.15)"
            />
            <Button
              style={{ top: '35px', right: '39px', position: 'absolute' }}
              position
              basic
              icon="redo"
              onClick={onClickRedo}
              color="rgba(34,36,38,.15)"
            />
          </SimpleCol>

          <SimpleCol>
            <div
              style={{
                background: '#EBEBEB',
                height: '100%',
                padding: '.78571429em 1em',
                borderRadius: '.28571429rem',
                overflow: 'auto',
              }}
            >
              {textOut}
            </div>
            <Button
              icon="copy outline"
              style={{
                bottom: '35px',
                right: '36.5px',
                position: 'absolute',
                background: '#EBEBEB',
                Index: 1000,
              }}
              onClick={onClickCopy}
            />
          </SimpleCol>
        </SimpleContainer>
      </div>
    </AppLayout>
  );
};

export default Template;
