import { all, takeLatest, fork, put, delay, call } from 'redux-saga/effects';
import faker, { fake } from 'faker';
import {
  TRANSLATE_SIMPLE_REQUEST,
  TRANSLATE_SIMPLE_SUCCESS,
  TRANSLATE_SIMPLE_FAILURE,
  LOGIN_REQUEST,
  LOGIN_SUCCESS,
  LOGIN_FAILURE,
  LOGOUT_REQUEST,
  LOGOUT_SUCCESS,
  LOGOUT_FAILURE,
} from '../reducers/translate';

function translateSimpleAPI(data) {
  return axios.post('/simple', data, { withCredentials: true });
}

function* translateSimple(action) {
  // const result = yield call(translateSimpleAPI, action.data,{ withCredentials: true });
  yield delay(100);
  try {
    // console.log('done');
    console.log(action.data);
    yield put({
      type: TRANSLATE_SIMPLE_SUCCESS,
      Output: faker.lorem.paragraphs() + faker.lorem.paragraphs(),
      Input: action.data,
      id: faker.random.number(),
    });
  } catch (error) {
    // console.log('fails');
    yield put({
      type: TRANSLATE_SIMPLE_FAILURE,
      error: error.response.data,
    });
  }
}

function* watchTranslateSimple() {
  yield takeLatest(TRANSLATE_SIMPLE_REQUEST, translateSimple);
}
function loginAPI(data) {
  return axios.post('/login', data);
}

function* login(action) {
  // const result = yield call(loginAPI, action.id,{ withCredentials: true });
  yield delay(100);
  try {
    // console.log('done');
    // console.log(action.id);
    yield put({
      type: LOGIN_SUCCESS,
      data: action.id,
    });
  } catch (error) {
    console.log('fails');
    yield put({
      type: LOGIN_FAILURE,
      error: error.response.data,
    });
  }
}

function* watchlogin() {
  yield takeLatest(LOGIN_REQUEST, login);
}
function logoutAPI(data) {
  return axios.post('/logout', data, { withCredentials: true });
}

function* logout(action) {
  // const result = yield call(logoutAPI, action.id);
  yield delay(100);
  try {
    console.log('done');
    yield put({
      type: LOGOUT_SUCCESS,
    });
  } catch (error) {
    console.log('fails');
    yield put({
      type: LOGOUT_FAILURE,
      error: error.response.data,
    });
  }
}

function* watchlogout() {
  yield takeLatest(LOGOUT_REQUEST, logout);
}

export default function* translateSaga() {
  console.log('watch Saga');
  yield all([fork(watchTranslateSimple), fork(watchlogin), fork(watchlogout)]);
}
