import React, { useCallback } from 'react';
import { useSelector, useDispatch } from 'react-redux';
import { List, Button, Icon } from 'semantic-ui-react';
import { REMOVE_SIMPLE_REQUEST } from '../reducers/translate';
const History = ({ onItemClick }) => {
  const { simple } = useSelector((state) => state.translate);

  const dispatch = useDispatch('');

  const onClickRemove = useCallback(
    (id) => () => {
      dispatch({ type: REMOVE_SIMPLE_REQUEST, id: id });
    },
    []
  );

  return (
    <>
      <List
        divided
        verticalAlign="middle"
        style={{
          background: '#e0e1e2 none',
          marginLeft: '40px',
          marginRight: '40px',
        }}
      >
        {simple.map((v) => (
          <List.Item>
            {v.Input}

            <List.Content floated="right">
              <Button
                style={{ zIndex: 1000 }}
                icon="trash alternate outline"
                onClick={onClickRemove(v.id)}
              />
            </List.Content>

            <List.Content onClick={onItemClick(v.Input, v.Output)}>
              {v.Input}
            </List.Content>
          </List.Item>
        ))}
      </List>
    </>
  );
};

export default History;
