import React, { useState, useCallback, useEffect } from 'react';
import { useSelector, useDispatch } from 'react-redux';
import AppLayout from '../components/AppLayout';
import { TRANSLATE_SIMPLE_REQUEST } from '../reducers/translate';
import {
  Form,
  TextArea,
  Button,
  Icon,
  Divider,
  Dropdown,
} from 'semantic-ui-react';
import styled from 'styled-components';
import useTextInput from '../hooks/useTextInput';

const SimpleCol = styled.div`
  width: 100%;
  padding: 10px 30px;
  height: 100%;
  position: relative;

  @media only screen and (max-width: 768px) {
    width: 100%;
    height: 50%;
    marin: 0 auto;
  }
`;

const SimpleContainer = styled.div`
  height: 80%;
  position: relative;
  display: flex;

  flex-direction: row;
  justify-content: space-between;
  @media only screen and (max-width: 768px) {
    height: 80%;
    display: block;
  }
`;

const Template = () => {
  const [textStart, onChangeStart] = useTextInput('');
  const [textMiddle, onChangeMiddle] = useTextInput('');
  const [textEnd, onChangeEnd] = useTextInput('');

  const [textOut, setTextOut] = useState('');
  const { simple, translateSimplesuccess } = useSelector(
    (state) => state.translate
  );
  const dispatch = useDispatch('');

  useEffect(() => {
    if (translateSimplesuccess) {
      setTextOut(simple[0].Output);
    }
  }, [translateSimplesuccess]);

  const onSubmit = useCallback(() => {
    console.log('submit');
    // console.log(text);
    if (
      textStart.trim('') === '' &&
      textMiddle.trim('') === '' &&
      textEnd.trim('') === ''
    ) {
      alert('내용을 입력하세요');
      return;
    }
    const result = textStart + textMiddle + textEnd;
    // dispatch({ type: TRANSLATE_SIMPLE_REQUEST, data: result });
  });

  const onItemClick = useCallback(
    (Input, Output) => () => {
      setTextOut(Output);
      setText(Input);
    },
    []
  );

  const onClickCopy = useCallback(() => {}, []);

  const onClickRedo = useCallback(() => {
    setText('');
  }, []);

  const options = [
    { key: 1, text: '결제', value: 1 },
    { key: 2, text: '홍보', value: 2 },
    { key: 3, text: '감사', value: 3 },
    { key: 4, text: '요청', value: 4 },
    { key: 5, text: '안부', value: 5 },
  ];
  return (
    <AppLayout>
      <Dropdown
        clearable
        options={options}
        selection
        style={{ marginTop: '10px', marginLeft: '30px' }}
      />

      <SimpleContainer>
        <SimpleCol>
          <Form
            style={{ height: '100%', position: 'relative' }}
            onSubmit={onSubmit}
          >
            <TextArea
              style={{ height: '22%', resize: 'none' }}
              placeholder="텍스트를 입력하세요..."
              onChange={onChangeStart}
              value={textStart}
            />
            <TextArea
              style={{ height: '50%', resize: 'none', marginTop: '15px' }}
              placeholder="텍스트를 입력하세요..."
              onChange={onChangeMiddle}
              value={textMiddle}
            />
            <TextArea
              style={{ height: '22%', resize: 'none', marginTop: '15px' }}
              placeholder="텍스트를 입력하세요..."
              onChange={onChangeEnd}
              value={textEnd}
            />
            <Button
              style={{ bottom: '5px', right: '9px', position: 'absolute' }}
              position
              basic
              icon="exchange"
              type="submit"
            ></Button>
          </Form>
          <Button
            style={{ top: '35px', right: '39px', position: 'absolute' }}
            position
            basic
            icon="redo"
            onClick={onClickRedo}
            color="rgba(34,36,38,.15)"
          />
          <Button
            style={{ top: '35px', right: '39px', position: 'absolute' }}
            position
            basic
            icon="redo"
            onClick={onClickRedo}
            color="rgba(34,36,38,.15)"
          />
          <Button
            style={{ top: '35px', right: '39px', position: 'absolute' }}
            position
            basic
            icon="redo"
            onClick={onClickRedo}
            color="rgba(34,36,38,.15)"
          />
        </SimpleCol>

        <SimpleCol>
          <div
            style={{
              background: '#EBEBEB',
              height: '100%',
              padding: '.78571429em 1em',
              borderRadius: '.28571429rem',
              overflow: 'auto',
            }}
          >
            {textOut}
          </div>
          <Button
            icon="copy outline"
            style={{
              bottom: '35px',
              right: '36.5px',
              position: 'absolute',
              background: '#EBEBEB',
              Index: 1000,
            }}
            onClick={onClickCopy}
          />
        </SimpleCol>
      </SimpleContainer>
    </AppLayout>
  );
};

export default Template;
