import React ,{useState,useCallback,useEffect}from 'react';

import { useSelector,useDispatch } from 'react-redux';
import AppLayout from '../components/AppLayout';
import History from '../components/History';
import {TRANSLATE_SIMPLE_REQUEST} from '../reducers/translate';
import { Form, TextArea, Button, Icon, Divider, List, Segment } from 'semantic-ui-react'
import styled, {ThemeProvider}from 'styled-components';
import theme from '../components/src/theme';


const SimpleContainer=styled.div`

    width:50%;


    @media ${(props)=>props.theme.mobile}{
        width:100%;
        marin :0 auto;
    }

`

const Main = () => {

    const [text, setText] = useState('');
    const [textOut, setTextOut] = useState('');
    const {simple,translateSimplesuccess} = useSelector(state => state.translate);
    const dispatch = useDispatch('');

    useEffect(() => {
        if(translateSimplesuccess){
            setTextOut(simple[0].Output);
        }

    }, [translateSimplesuccess])

    const onSubmit=useCallback(
        () => {
            console.log("submit");
            console.log(text);
           if(text.trim('')===''){
               alert('내용을 입력하세요');
               return;
           }
           dispatch({type:TRANSLATE_SIMPLE_REQUEST,data:text});


        },
        [text],
    )
    const onChangeText=useCallback(

        (e) => {
           setText(e.target.value);

        },
        [text],
    )

    const onItemClick = useCallback(
        (Input,Output)=>() => {
        setTextOut(Output);
           setText(Input);
        },
        [],
    )
    return (

        <AppLayout > 
            <ThemeProvider theme={theme}> 

            <div  style={{ padding: "40px", height: "650px" ,position:'relative' }}>

<SimpleContainer >
    <Form style={{ height: "100%"}} onSubmit={onSubmit}>
        <TextArea style={{ height: "100%" }} placeholder='텍스트를 입력하세요...' onChange={onChangeText} value={text}/>

        <Button basic icon style ={{zIndex:1000}}labelPosition='left' type='submit'  color='rgba(34,36,38,.15)' >
            <Icon name='play' basic  /> 번환
        </Button>
    </Form>



</SimpleContainer>


<SimpleContainer>

    <div style={{ background: "#EBEBEB", height: "100%", padding: '.78571429em 1em', borderRadius: '.28571429rem' }}>

    {textOut}
    </div>

</SimpleContainer>

</div>


<div style={{ marginLeft: '40px'}}> 검색내역</div>
<Divider style={{ marginLeft: '40px', marginRight: '40px' }} />
<div>
<History onItemClick={onItemClick}/>
</div>


            </ThemeProvider>



        </AppLayout >

    )
}

export default Main;
