import React, { useCallback, useState, useEffect } from 'react';
import { GoogleLogout } from 'react-google-login';
import { LOGOUT_REQUEST } from '../reducers/translate';
import { useSelector, useDispatch } from 'react-redux';
import { Button, Icon, Form, Container } from 'semantic-ui-react';
import styled from 'styled-components';
const Overlay = styled.div`
  position: fixed;
  top: 0;
  left: 0;
  right: 0;
  bottom: 0;
  z-index: 5;
  background: rgba(0, 0, 0, 0.45);
  display: flex;
  align-items: center;
  justify-content: center;
`;
const ContainForm = styled.div`
  padding: 20px;
  background: #ffffffff;
  display: flex;
  flex: 0.5;
  border: 1px solid rgba(34, 36, 38, 0.15);
  flex-direction: column;
`;

const SendMail = () => {
  return (
    <>
      <Overlay>
        <ContainForm>
          <Form>
            <Form.Field>
              <label>E-mail</label>
              <input placeholder="Enter the Email Address " />
            </Form.Field>
            <Form.Field>
              <label>Subject</label>
              <input placeholder="Subject here.." />
            </Form.Field>
            <Form.TextArea
              style={{ resize: 'none' }}
              label="Content"
              placeholder="Enter the text here..."
            />
            <Button type="submit">Submit</Button>
          </Form>
        </ContainForm>
      </Overlay>
    </>
  );
};
export default SendMail;
