import React, { useState, useCallback, useEffect } from 'react';

import { useSelector, useDispatch } from 'react-redux';
import AppLayout from '../components/AppLayout';
import History from '../components/History';
import { TRANSLATE_SIMPLE_REQUEST } from '../reducers/translate';
import { Form, TextArea, Button, Icon, Divider } from 'semantic-ui-react';
import styled, { ThemeProvider } from 'styled-components';

const SimpleCol = styled.div`
  width: 100%;
  padding: 30px;
  height: 100%;

  @media only screen and (max-width: 768px) {
    width: 100%;
    height: 60%;
    marin: 0 auto;
  }
`;

const SimpleContainer = styled.div`
  height: 70%;
  position: relative;
  display: flex;
  flex-direction: row;
  justify-content: space-between;
  @media only screen and (max-width: 768px) {
    height: 40%;
    display: block;
  }
`;

const ListContainer = styled.div`
  height: 30%;
  margin-left: 40px;
  margin-right: 40px;
  position: relative;

  @media only screen and(max-width:768px) {
  }
`;

const Main = () => {
  const [text, setText] = useState('');
  const [textOut, setTextOut] = useState('');
  const { simple, translateSimplesuccess } = useSelector(
    (state) => state.translate
  );
  const dispatch = useDispatch('');

  useEffect(() => {
    if (translateSimplesuccess) {
      setTextOut(simple[0].Output);
    }
  }, [translateSimplesuccess]);

  const onSubmit = useCallback(() => {
    console.log('submit');
    console.log(text);
    if (text.trim('') === '') {
      alert('내용을 입력하세요');
      return;
    }
    dispatch({ type: TRANSLATE_SIMPLE_REQUEST, data: text });
  }, [text]);
  const onChangeText = useCallback(
    (e) => {
      setText(e.target.value);
    },
    [text]
  );

  const onItemClick = useCallback(
    (Input, Output) => () => {
      setTextOut(Output);
      setText(Input);
    },
    []
  );
  return (
    <AppLayout>
      <div style={{ position: 'fixed', height: '100%', width: '100%' }}>
        <SimpleContainer>
          <SimpleCol>
            <Form style={{ height: '100%' }} onSubmit={onSubmit}>
              <TextArea
                style={{ height: '100%' }}
                placeholder="텍스트를 입력하세요..."
                onChange={onChangeText}
                value={text}
              />

              <Button
                position
                basic
                icon
                labelPosition="left"
                type="submit"
                color="rgba(34,36,38,.15)"
              >
                <Icon name="play" basic /> 번환
              </Button>
            </Form>
          </SimpleCol>

          <SimpleCol>
            <div
              style={{
                background: '#EBEBEB',
                height: '100%',
                padding: '.78571429em 1em',
                borderRadius: '.28571429rem',
                overflow: 'auto',
              }}
            >
              {textOut}
            </div>
          </SimpleCol>
        </SimpleContainer>

        <ListContainer>
          <div> 검색내역</div>
          {/* <Divider /> */}
          <History onItemClick={onItemClick} style={{ overflow: 'scroll' }} />
        </ListContainer>
      </div>
    </AppLayout>
  );
};

export default Main;
